// useArray.cpp -- a program that demonstrates the use of the Array class#include <iostream>#include "Array.h"using namespace std;Array<int> getElements( );float calcAverage( Array<int> avnums );int main( ){	Array<int> nums(2);	nums = getElements( );	Array<int> nums2 = nums;	float average = calcAverage( nums2 );	cout << "The average is: " << average << endl;	return 0;}Array<int> getElements(  ){	cout << "getElements called." << endl;	Array<int> numbers( 2 );	int i = 0;	cout << "Enter a positive integer: ";	cin >> numbers[ i ];	while ( numbers[ i ] != -1 ) {		i++;		if ( i == numbers.length( ) )			numbers.changeSize( i * 2 );		cout << "Enter a positive integer (enter -1 to stop): ";		cin >> numbers[ i ];		}		numbers.changeSize( i );	return numbers;}float calcAverage( Array<int> avnums ){	cout << "calcAverage called." << endl;	int sum = 0;	for ( int i = 0; i < avnums.length( ); i++ ) 		sum += avnums[ i ];	return sum / float( avnums.length( ) );}	