// useCheckbook.cpp - A program for using the Checkbook class#include <iostream>#include <iomanip>#include "MinBalCheckBook.h"	using namespace std;int menu( );const int CHECK = 1, DEPOSIT = 2, BALANCE = 3, QUIT = 4; int main( ){	float balance;		cout << "Enter the initial balance: $";	cin >> balance;	MinBalCheckBook<float> cb( balance, 200.00F, 10.00F, 0.50F );	float amount;	int choice;	bool checkAccepted = false;	cout << fixed << showpoint << setprecision( 2 );	choice = menu( );		while ( choice != QUIT )  {		if ( choice == CHECK ) {			cout << "Enter check amount: $";		 	cin >> amount;			if ( cb.writeCheck( amount ) ) {				cout << "Check accepted." << endl;				checkAccepted = true;				}			else {				cout << "Your balance is not high ";				cout << "enough for that check." << endl; 				}			}		else if ( choice == DEPOSIT ) {			cout << "Enter deposit amount: $";			cin >> amount;			cb.deposit( amount );			cout << "Deposit accepted." << endl;			}			else  {   // must be a balance request			amount = cb.getBalance( );			cout << "Your balance is: $" << amount << endl;			}		choice = menu( );		}	if ( checkAccepted ) {		Array<float> ci = cb.getChecks( );		cout << "Your checks are:" << endl;		for ( int i = 0; i < ci.length( ); i++ )			cout << "$" << ci[ i ] << endl;		}	else		cout << "No checks were accepted." << endl;	return 0;}				int menu( ){	int choice;	cout << endl;	cout << "1	Write a check" << endl;	cout << "2	Make a deposit" << endl;	cout << "3	Get the balance" << endl;	cout << "4	Quit" << endl << endl;	cout << "Enter a number between 1 and 4: ";	cin >> choice;	return choice;}