#include <iostream>#include "PriorityQueue.h"using namespace std;struct MyStruct {	int id;	float amount;	bool operator >( const MyStruct & right ) { return amount > right.amount; }};template <class DataType>void heapsort( Array<DataType> & arr );int main( ){	int num;	cout << "How many records do you want to enter? ";	cin >> num;	Array<MyStruct> objs( num );	for ( int i = 0; i < objs.length( ); i++ ) {		cout << "Enter id for record " << i + 1 << ": ";		cin >> objs[ i ].id;		cout << "Enter amount for record " << i + 1 << ": ";		cin >> objs[ i ].amount;		}	heapsort( objs );	cout << "Records in sorted order:" << endl;	for ( int i = 0; i < objs.length( ); i++ ) {		cout << "record " << i << ":" << endl;		cout << "	id:     " << objs[ i ].id << endl;		cout << "	amount: " << objs[ i ].amount << endl;	}	return 0;}template <class DataType>void heapsort( Array<DataType> & arr ){	PriorityQueue<DataType> pq( arr );	for ( int i = arr.length( ) - 1; i >= 0; i-- )		pq.dequeue( arr[ i ] );}